import "../Button/button.css";
import { useState, useEffect } from "react";
import axios from "../../Api/axios";
import useAuth from "../../hooks/useAuth";
import { useForm } from "react-hook-form";

const ProfilePage = () => {
	const { auth } = useAuth();
	const [profile, setProfile] = useState({});
	const id = auth.userId;
	useEffect(() => {
		const getProfile = async () => {
			const response = await axios.get(`auth/profile/${id}`, { headers: { Authorization: `Bearer ${auth.token}` } });
			const profile = response.data;
			setProfile(response.data);
			console.log("profile", response.data);
		};
		getProfile();
	}, [auth.token, id]);

	const HandleInput = async (data) => {
		const formData = new FormData();
		formData.append("firstName", data.firstName);
		formData.append("lastName", data.lastName);
		formData.append("position", data.position);
		formData.append("department", data.department);
		formData.append("email", data.email);
		try {
			const response = await axios.put(`auth/profile/${id}`, formData, {
				headers: { Authorization: `Bearer ${auth.token}` },
			});
			console.log(response.data);
		} catch (error) {
			console.log(error);
		}
	};

	const { register, handleSubmit, reset } = useForm();

	return (
		<div className="h-max mt-4 p-2 w-full flex flex-col  object-top border-2 border-tertiary border-b-8 border-r-8 rounded-2xl">
			<h1 className="text-primary text-xl pb-1">Profil de l'utilisateur : </h1>
			<form onSubmit={handleSubmit(HandleInput)} className="flex flex-col gap-1">
				<div>
					<label className="text-primary" htmlFor="firstName">
						Prénom :{" "}
					</label>
					<input
						className="text-tertiary "
						type="text"
						name="firstName"
						id="firstName"
						placeholder="Prénom"
						defaultValue={profile.firstName}
						{...register("firstName")}
					/>
				</div>
				<div>
					<label className="text-primary" htmlFor="lastName">
						Nom :{" "}
					</label>
					<input type="text" name="lastName" id="lastName" className="text-tertiary " {...register("lastName")}>
						{profile.lastName}
					</input>
				</div>
				<div>
					<label className="text-primary" htmlFor="position">
						Poste :{" "}
					</label>
					<input type="text" name="position" id="position" className="text-tertiary " defaultValue={profile.position} {...register("position")} />
				</div>
				<div>
					<label className="text-primary" htmlFor="department">
						Département :{" "}
					</label>
					<input type="text" name="department" id="department" className="text-tertiary " defaultValue={profile.department} {...register("department")} />
				</div>
				<div>
					<label className="text-primary" htmlFor="email">
						Email :{" "}
					</label>
					<input type="email" name="email" id="email" className="text-tertiary " defaultValue={profile.email} {...register("email")} />
				</div>
				<label className="text-primary" htmlFor="bio">
					Bio :{" "}
				</label>
				<textarea name="Bio" id="Bio" rows="5" {...register("Bio")} defaultValue={profile.bio} className="text-tertiary " />
				<button className="brutal-btn">Enregistrer les modifications</button>
			</form>
		</div>
	);
};

export default ProfilePage;
