import "../Button/button.css";
import { useState, useEffect } from "react";
import axios from "../../Api/axios";
import useAuth from "../../hooks/useAuth";

const ProfilePage = () => {
	const { auth } = useAuth();
	const [profile, setProfile] = useState({});
	const id = auth.userId;
	useEffect(() => {
		const getProfile = async () => {
			const response = await axios.get(`auth/profile/${id}`, { headers: { Authorization: `Bearer ${auth.token}` } });
			setProfile(response.data);
			console.log("profile", response.data);
		};
		getProfile();
	}, [auth.token]);

	return (
		<div className="flex flex-col items-center  brutal-btn">
			<h1 className="text-black text-4xl">Profil de l'utilisateur</h1>
			<p className="text-primary text-9xl">NYI</p>
		</div>
	);
};

export default ProfilePage;
