import { useState, useEffect, useRef, useCallback } from "react";
import axios from "../Api/axios";
import useAuth from "../hooks/useAuth";
import usePostsRefresh from "../hooks/usePostsRefresh";
import LikeButton from "./Button/LikeButton";
import Delete from "./Button/DeletePost";
import Modal from "./Button/Modal";
import ElapsedTime from "./Utilities/ElapsedTime";

const Posts = () => {
	const { refresh, setRefresh, postId } = usePostsRefresh();
	const { auth } = useAuth();
	const [pageNumber, setPageNumber] = useState(1);
	const [results, setResults] = useState([]);
	const [isLoading, setIsLoading] = useState(false);
	const [error, setError] = useState({});
	const [hasNextPage, setHasNextPage] = useState(false);
	const [isError, setIsError] = useState(false);
	const [freshPages, setFreshPages] = useState([]);

	useEffect(() => {
		setIsLoading(true);
		setIsError(false);
		setError({});

		let isMounted = true;

		const getPosts = async () => {
			try {
				if (refresh) {
					setRefresh(false);
					setResults([]);
					let freshPages = [];
					for (let i = 1; i <= pageNumber; i++) {
						const response = await axios.get(`/post/page/${i}`, {
							headers: {
								Authorization: `Bearer ${auth.token}`,
							},
						});
						setFreshPages((freshPages) => [...freshPages, response.data]);
						console.log("fresh pages", freshPages);
						isMounted && setResults(freshPages);
						setHasNextPage(Boolean(response.data.length));
						setIsLoading(false);
					}
					console.log("fresh posts", freshPages);
				} else {
					const response = await axios.get(`/post/page/${pageNumber}`, { headers: { Authorization: `Bearer ${auth.token}` } });
					isMounted && setResults((prev) => [...prev, ...response.data]);
					setHasNextPage(Boolean(response.data.length));
					setIsLoading(false);
				}
			} catch (error) {
				setIsLoading(false);
				setIsError(true);
				setError({ message: error.message });
			}
		};
		getPosts();

		return () => {
			isMounted = false;
		};
	}, [auth.token, pageNumber, refresh, setRefresh]);

	let lastPost = "";

	const LoadNextPage = () => {
		const intObserver = useRef();
		lastPost = useCallback((post) => {
			if (isLoading) return;
			if (intObserver.current) intObserver.current.disconnect();

			intObserver.current = new IntersectionObserver((results) => {
				if (results[0].isIntersecting && hasNextPage) {
					console.log("Last post");
					setPageNumber((prev) => prev + 1);
				}
			});
			if (post) intObserver.current.observe(post);
		});
	};

	LoadNextPage();

	return (
		<article className="text-tertiary text-lg">
			<h2 className="h-min mt-4 p-4 w-full flex flex-col justify-between object-top border-2 text-primary font-bold text-3xl border-primary border-b-8 border-r-8 rounded-2xl">
				Contributions
			</h2>
			{results?.length ? (
				<ul>
					{results.map((post, i) => (
						<li
							ref={lastPost}
							className="h-min mt-4 p-4 w-full flex flex-col justify-between object-top border-2 border-tertiary border-b-8 border-r-8 rounded-2xl"
							key={i}
						>
							<div className="flex flex-row justify-between">
								<div>{post.userId}</div>
								<div className="text-base">Il y a {ElapsedTime(post?.createdAt)}</div>
							</div>
							<div className="bg-secondary mt-2 p-4 border-tertiary border-2">{post?.content}</div>
							<div>{post?.imageUrl && <img src={post.imageUrl} alt="post" className=" h-80 mt-4 mx-auto object-cover rounded-2xl" />}</div>
							<div className="flex justify-between mt-4">
								<div className="flex flex-row">
									<LikeButton post={post} />
									{post.likes !== 0 && <p className="pl-2 pt-2">{post.likes}</p>}
								</div>
								<div className="flex flex-row gap-x-4  ">
									<div className="flex flex-row ">{(post.userId === auth.userId || auth.role === "admin") && <Modal post={post} />}</div>
									<div className="flex flex-row ">{(post.userId === auth.userId || auth.role === "admin") && <Delete post={post} />}</div>
								</div>
							</div>
						</li>
					))}
				</ul>
			) : (
				<p>Aucun post Ã  afficher...</p>
			)}
		</article>
	);
};

export default Posts;
