import "../Button/button.css";
import { useForm } from "react-hook-form";
import axios from "../../Api/axios";

const POST_URL = "/api/post";

const handleInput = async (data) => {
	try {
		const post = await axios.post(POST_URL, { name: post.name, title: post.title, content: post.content, imageUrl: post.imageUrl });
		console.log(JSON.stringify(post?.data));
	} catch (error) {
		console.log(error);
		console.log(error.response?.data);
	}
};

const PostsPageAddPost = () => {
	return (
		<form className="h-min mt-2 w-full text-lg flex flex-col justify-between object-top border-2 border-primary border-b-8 border-r-8 rounded-2xl ">
			<label className="pl-4 pt-4 pb-1 font-bold text-primary">Exprimez-vous !</label>
			<textarea className="px-4 mx-4 h-32 flex text-wrap bg-secondary text-tertiary  border-1 border-black resize-none" placeholder="..." />
			<div className="flex justify-end">
				<div>
					<button className="brutal-btn m-4">Ajouter une image</button>
					<button className="brutal-btn m-4">Poster</button>
				</div>
			</div>
		</form>
	);
};

export default PostsPageAddPost;
