import { createContext, useState } from "react";

const NavIsOpenedContext = createContext({});

export const NavIsOpenedProvider = ({ children }) => {
	const [navMenuOpenened, setNavMenuOpenened] = useState(false);

	const handleNavMenuOpen = () => {
		setNavMenuOpenened(!navMenuOpenened);
	};

	return <NavIsOpenedContext.Provider value={{ navMenuOpenened, setNavMenuOpenened, handleNavMenuOpen }}>{children}</NavIsOpenedContext.Provider>;
};

export default NavIsOpened;
