import { createContext, useState } from "react";

const NavIsOpenedContext = createContext({});

export const NavIsOpenedProvider = ({ children }) => {
	const [navMenuOpenened, setNavMenuOpenened] = useState(false);

	return <NavIsOpenedContext.Provider value={{ navMenuOpenened, setNavMenuOpenened }}>{children}</NavIsOpenedContext.Provider>;
};

export default NavIsOpenedContext;
