import { useState } from "react";
import EditProfile from "./EditProfile.jsx";
import ProfilePage from "../ProfilePageComponents/ProfilePageContent.jsx";
import CloseProfileModalButton from "./CloseProfileModalButton.jsx";
import { set } from "react-hook-form";

const ProfileModal = () => {
	const [showProfileModal, setShowProfileModal] = useState(false);

	return (
		<>
			<EditProfile setShowProfileModal={setShowProfileModal} />
			{showProfileModal ? (
				<>
					<div className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none ">
						<div className="relative w-10/12 max-h-screen  mx-auto max-w-3xl">
							<div className="h-max mt-4 p-4 w-full flex flex-col  object-top border-2 border-tertiary border-b-8 border-r-8 rounded-2xl bg-white">
								<div className="flex flex-row justify-between">
									<h1 className="text-primary text-xl pb-1">Profil de l'utilisateur </h1>
									<CloseProfileModalButton onclick={() => setShowProfileModal(false)} />
								</div>
								<ProfilePage setShowModal={setShowProfileModal} />
								{/* </div> */}
							</div>
						</div>
					</div>
					<div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
				</>
			) : null}
		</>
	);
};

export default ProfileModal;
