import { useState, useEffect } from "react";
import axios from "../Api/axios";
import useAuth from "../hooks/useAuth";
import usePostsRefresh from "../hooks/usePostsRefresh";

const Posts = () => {
	const { refresh } = usePostsRefresh();
	const [posts, setPosts] = useState();
	const { auth } = useAuth();
	useEffect(() => {
		let isMounted = true;

		const getPosts = async () => {
			try {
				const response = await axios.get("/post", { headers: { Authorization: `Bearer ${auth.token}` } });
				console.log(response.data);
				const sortedPosts = response.data.sort((a, b) => {
					return new Date(b.createdAt) - new Date(a.createdAt);
				});
				isMounted && setPosts(sortedPosts);
			} catch (error) {
				console.log(error);
			}
		};
		getPosts();

		return () => {
			isMounted = false;
		};
	}, [refresh, auth.token]);

	return (
		<article>
			<h2 className="h-min mt-4 w-full flex flex-col justify-between object-top border-2 border-primary border-b-8 border-r-8 rounded-2xl ">Posts List</h2>
			{posts?.length ? (
				<ul>
					{posts.map((post, i) => (
						<li className="h-min mt-4 w-full flex flex-col justify-between object-top border-2 border-primary border-b-8 border-r-8 rounded-2xl " key={i}>
							{post?.createdAt}
							{post?.content}
							<img src={post?.imageUrl} alt={post?.name} className="h-96 w-full object-cover" />
						</li>
					))}
				</ul>
			) : (
				<p>No posts to display</p>
			)}
		</article>
	);
};

export default Posts;
