import "../Button/button.css";
import { useState, useEffect } from "react";
import axios from "../../Api/axios";
import useAuth from "../../hooks/useAuth";

const ProfilePage = () => {
	const { auth } = useAuth();
	useEffect(() => {
		const getProfile = async () => {
			const [profile, setProfile] = useState({});
			const response = await axios.get(`/user/profile`, { headers: { Authorization: `Bearer ${auth.token}` } });
			setProfile(response.data);
		};
		getProfile();
	}, [auth.token, profile]);

	return (
		<div className="flex flex-col items-center  brutal-btn">
			<h1 className="text-black text-4xl">Profil de l'utilisateur</h1>
			<p className="text-primary text-9xl">NYI</p>
		</div>
	);
};

export default ProfilePage;
