import usePostsRefresh from "../../hooks/usePostsRefresh";
import useAuth from "../../hooks/useAuth";
import "../Button/button.css";
import axios from "../../Api/axios";
import PostsPageAddPost from "../PostsPageComponents/PostsPageAddPost";

const EditPost = ({ post }) => {
	const { refresh, setRefresh } = usePostsRefresh();
	const { auth } = useAuth();

	const editPost = async () => {
		const id = post._id;
		try {
			await axios.delete(`/post/${id}`, { headers: { Authorization: `Bearer ${auth.token}` } });
			setRefresh(!refresh);
		} catch (error) {
			console.log(error);
		}
	};
	return (
		<>
			<button onClick={editPost} className="brutal-btn">
				Modifier le post
			</button>
			<PostsPageAddPost />
		</>
	);
};

export default EditPost;
