import "../Button/button.css";
import { useState, useEffect } from "react";
import axios from "../../Api/axios";
import useAuth from "../../hooks/useAuth";
import { useForm } from "react-hook-form";

const ProfilePage = () => {
	const { auth } = useAuth();
	const [profile, setProfile] = useState({});
	const id = auth.userId;
	const [refreshData, setRefreshData] = useState(false);

	useEffect(() => {
		let isMounted = true;

		const getProfile = async () => {
			try {
				const response = await axios.get(`auth/profile/${id}`, { headers: { Authorization: `Bearer ${auth.token}` } });
				const profile = response.data;
				isMounted && setProfile(profile);
				console.log("profile", response.data);
			} catch (error) {
				console.log(error);
			}
		};
		getProfile();
		return () => {
			isMounted = false;
		};
	}, [id, auth.token, refreshData]);

	const HandleInput = async (data) => {
		const formData = new FormData();
		formData.append("firstName", data.firstName);
		formData.append("lastName", data.lastName);
		formData.append("position", data.position);
		formData.append("department", data.department);
		formData.append("bio", data.bio);
		console.log("formData", formData);
		try {
			const response = await axios.put(`auth/profile/${id}`, formData, {
				headers: { Authorization: `Bearer ${auth.token}` },
			});
			console.log(response);
			setRefreshData(!refreshData);
			reset();
		} catch (error) {
			console.log(error);
		}
	};

	const { register, handleSubmit, reset } = useForm();

	return (
		<div className="h-max mt-4 p-2 w-full flex flex-col  object-top border-2 border-tertiary border-b-8 border-r-8 rounded-2xl">
			<h1 className="text-primary text-xl pb-1">Profil de l'utilisateur : </h1>
			<form onSubmit={handleSubmit(HandleInput)} className="flex flex-col gap-1">
				<div>
					<label className="text-primary" htmlFor="firstName">
						Prénom :{" "}
					</label>
					<input
						className="text-tertiary "
						type="text"
						name="firstName"
						id="firstName"
						value={profile.firstName}
						onChange={(e) => setProfile({ ...profile, firstName: e.target.value })}
						{...register("firstName")}
					/>
				</div>
				<div>
					<label className="text-primary" htmlFor="lastName">
						Nom :{" "}
					</label>
					<input type="text" name="lastName" id="lastName" className="text-tertiary " value={profile.lastName} {...register("profile.lastName")} />
				</div>
				<div>
					<label className="text-primary" htmlFor="position">
						Poste :{" "}
					</label>
					<input type="text" name="position" id="position" className="text-tertiary " value={profile.position} {...register("position")} />
				</div>
				<div>
					<label className="text-primary" htmlFor="department">
						Département :{" "}
					</label>
					<input type="text" name="department" id="department" className="text-tertiary " value={profile.department} {...register("department")}></input>
				</div>

				<label className="text-primary" htmlFor="bio">
					Bio :{" "}
				</label>
				<textarea name="bio" id="bio" rows="5" {...register("bio")} value={profile.bio} className="text-tertiary " />

				<button className="brutal-btn">Enregistrer les modifications</button>
			</form>
		</div>
	);
};

export default ProfilePage;
